import React from 'react';
import './App.css';

interface State {
                   nayta? : boolean,
                   summa? : number;
                }


export default class App extends React.Component { 

  state : State = {
                  nayta : false,
                  summa : 0
                }

  paivita = (event : any) : void => {

    this.setState({
                  [event.target.name] : event.target.value,
                  nayta : false
     });


  }

  tippiPihi = () : void => {

    this.setState({
                    nayta : true
                   });


  }

  tippiNormal = () : void => {

    this.setState({
                    nayta : true
                   });


  }

  tippiReilu = () : void => {

    this.setState({
                    nayta : true
                   });


  }
  render() {

    let tippi;

    if (this.state.nayta === true ) {

      tippi = <div className="alert alert-success">Tippaa: {this.state.summa} â‚¬</div>;

    }
  return (
    <div className="container">
      <h1>Tippilaskuri</h1>

      <input className="form-ontrol mt-2" type="text" name="summa" onChange={this.paivita} /> <br></br><br></br>

      <button className="btn btn-danger" onClick={() => { this.tippiPihi() }}>Pihi (10%)</button> &nbsp;
      <button className="btn btn-primary" onClick={() => { this.tippiNormal() }}>Normaali (15%)</button>&nbsp;
      <button className="btn btn-success" onClick={() => { this.tippiReilu() }}>Reilu (20%)</button>


      { tippi }

    </div>
  )
}
}
