import React from 'react';
import './App.css';

interface State {
                   nayta? : boolean,
                   nimi? : string
                }


export default class App extends React.Component { 

  state : State = {
                  nayta : false,
                  nimi : ""
                }

  paivita = (event : any) : void => {

    this.setState({
                  [event.target.name] : event.target.value,
                  nayta : false
     });


  }

  sanoHeippa = () : void => {

    this.setState({
                    nayta : true
                   });


  }
  render() {

    let tervehdys;

    if (this.state.nayta === true ) {

      tervehdys = <div className="alert alert-success">Heippa, {this.state.nimi}!</div>;

    }
  return (
    <div className="container">
      <h1>Demo 1: Hello World!</h1>

      <input className="form-ontrol mt-2" type="text" name="nimi" onChange={this.paivita} /> <br></br><br></br>

      <button className="btn btn-primary" onClick={() => { this.tippiPihi() }}>Sano heippa!</button><br/> <br/>
      <button className="btn btn-primary" onClick={() => { this.tippiNormal() }}>Sano heippa!</button><br/> <br/>
      <button className="btn btn-primary" onClick={() => { this.tippiReilu() }}>Sano heippa!</button><br/> <br/>


      { tervehdys }

    </div>
  )
}
}
